import { createSlice } from "@reduxjs/toolkit";

export const sliceProduction = createSlice({
    name: "printArry",
    initialState: {
        array: [
            { id: 1, name: "A" },
            { id: 2, name: "B" },
            { id: 3, name: "C" },
            { id: 4, name: "D" },
        ],

    },
    reducers: {
        setNewArr: (state, val) => {
            state.array = val.payload
        },
        addNewObj :(state,val)=>{
            state.array = val.payload
        }
    }
})
export const { setNewArr ,addNewObj} = sliceProduction.actions

export const listList = createSlice({
    name:"dddd",
    initialState:[],
