import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function FormSt() {
    const style = {
        width: "600px",
        margin: "30px"
    }

    const refID = useRef()
    const refName = useRef()
    const refProfile = useRef()
    const refNumber = useRef()

    const clearInput = ""

    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { name: refName.current.value, profile: refProfile.current.value, number: refNumber.current.value }
        if (!obj.name || !obj.profile || !obj.number) {
            alert("All input are not fill")
            return
        }
        dispach(addData(obj))

        refName.current.value = clearInput
        refProfile.current.value = clearInput
        refNumber.current.value = clearInput
    }
    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control ref={refID} type="number" placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control type="text" placeholder="Profile" />
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    );
}

export default FormSt;
