import { useContext } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { ProductContext } from '../../../Pages/Products/productProvider';
import '../Product.css'

// const hello = 'hello'
function ProductModal({ show, handleClose }) {
    const context = useContext(ProductDataProvider)
    const param = useParams()

    const [formVal, setFormVal] = useState({
        type: '',
        id: Math.random(),
        productName: '',
        price: '',
        madeIn: '',
        discription: ' ',
        url:''
    })

    const changeHandler = (e) => {
        const updatedForm = { ...formVal }
        updatedForm[e.target.name] = e.target.value;
        setFormVal(updatedForm)
    }
    const submitForm = (e) => {
        e.preventDefault()
        context.dispatch({ type: param.type, paylode: formVal })
        setShow(false);
        setFormVal({
            type: '',
            id: Math.random(),
            url: '',
            price: '',
            description: ' '
    
        })

    }
    return (
        <Modal show={show} onHide={handleClose}>
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Type</Form.Label>
                    <Form.Control type="Type" placeholder="Enter email" onChange={changeHandler}/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Product Name</Form.Label>
                    <Form.Control type="text" placeholder="productName"  onChange={changeHandler}/>
                </Form.Group>

                <Button>
                    Submit
                </Button>
            </Form>
        </Modal>
    );
}

export default ProductModal;
