import { useEffect, useRef, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { AddingData, UpdatetdState } from '../centerSlcie/newSlice';

function FormSt({ btn }) {
    const style = {
        width: "600px",
        margin: "30px"
    }
    const localStore = useSelector((state) => {
        return state
    })
    const params = useParams()
    const [storege, setStorege] = useState({
        id: "",
        name: "",
        profile: "",
    })

    const clone = { ...storege }
    const chengeHandle = (e) => {
        clone[e.target.name] = e.target.value
        setStorege(clone)
    }
    useEffect(() => {
        if (btn.name === "Update") {
            const newData = localStore.data.find((item) => {
                return item.id === params.id
            })

            setStorege(newData)

        }
    }, [params])


    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { id: storege.id, name: storege.name, profile: storege.profile }
        if (!obj.name || !obj.profile || !obj.id) {
            alert("All input are not fill")
            return
        } if (btn.name === 'Update') {
            const index = localStore.data.findIndex(object => {
                return object.id === obj.id;
            });
           
            const updateData = [...localStore.data]
            updateData.splice(index, 1, obj);
            dispach(UpdatetdState(localStore))
            setStorege({ id: "", name: "", profile: "" })
            return

        }
        console.log(obj);
        dispach(AddingData(obj))
        setStorege({ id: "", name: "", profile: "" })

    }

    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control value={storege.id} onChange={chengeHandle} type="number" name='id' placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control value={storege.name} type="text" onChange={chengeHandle} name='name' placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control value={storege.profile} type="text" onChange={chengeHandle} name='profile' placeholder="Profile" />
            </Form.Group>

            <Button variant={btn.class} type="button" onClick={AddObject}>
                {btn.name}
            </Button>
        </Form>
    );
}

export default FormSt;
