import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";

function EditForm() {
    const params = useParams()
    const [value, setValue] = useState({
        id: "",
        name: "",
        email: "",
        addres: "",
    })
    const chengeHandller = (e) => {
        const clone = { ...value }
        clone[e.target.name] = e.target.value
    }
    const fechingData = async () => {
        const res = await axios.get(`http://localhost:5000/contact/${params.id}`)
        setValue(res.data)
    }
    useEffect(() => {
        fechingData()
    }, [params])

    return <form className="w-50 p-3">
        <div className="mb-3">
            <label htmlFor="ID" className="form-label">ID</label>
            <input type="number" value={value.id} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={chengeHandller} name='id'/>
        </div>
        <div className="mb-3">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" value={value.name} className="form-control" id="exampleInputPassword1" onChange={chengeHandller} name='name' />
        </div>
        <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="text" value={value.email} className="form-control" id="exampleInputPassword1" onChange={chengeHandller} name='email'/>
        </div>
        <div className="mb-3">
            <label htmlFor="addres" className="form-label">Addres</label>
            <input type="text" value={value.addres} className="form-control" id="exampleInputPassword1" onChange={chengeHandller} name='addres'/>
        </div>

        <button type="button" className="btn btn-info">Submit</button>
    </form>
}
export default EditForm
