import { createSlice } from "@reduxjs/toolkit";

export const comboSlice = createSlice({
    name: "combo Slice",
    initialState: {
        tabaleData: []
    },
    reducers: {
        addData: (state, val) => {
            const clone = [...state.tabaleData , val.payload]
            state.tabaleData = clone
            console.log(clone);
            console.log(state);
        }
    }
})
export const { addData } = comboSlice.actions
