import axios from "axios"
import { useEffect, useState } from "react";
import NewsItem from "./NewsItem"

function MainNews() {
    const [value, setValue] = useState(null)


    const getData = () => {
        axios.get(`https://hn.algolia.com/api/v1/search?query=react&page=0`)
            .then((res) => {
                setValue(res.data.hits);
                // console.log(res.data.hits);
            })
    }
    useEffect(() => {
        getData()
    }, [])
    return (
        <div className="News-set">
            {/* <NewsItem/> */}
            {!value ? <div id="root">
                <div className="divLoader">
                    <svg className="svgLoader" viewBox="0 0 1024 1024" width="10em" height="10em">
                        <path fill="lightblue"
                            d="PATH FOR THE LOADER ICON"
                        />
                    </svg>
                </div>
            </div> : value.map((item, i) => {
                return <NewsItem key={i} data={item} />
            })}
        </div>
    )
}
export default MainNews
