import { useEffect, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { AddingData } from '../centerSlcie/newSlice';

function FormSt() {
    const style = {
        width: "600px",
        margin: "30px"
    }
    const params = useParams()

    const refID = useRef()
    const refName = useRef()
    const refProfile = useRef()
    
    useEffect(() => {
        refName.current.value = "ss"
        refProfile.current.value = ""
        refID.current.value = ""
    }, [params])


    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { id: refID.current.value, name: refName.current.value, profile: refProfile.current.value }
        if (!obj.name || !obj.profile || !obj.id) {
            alert("All input are not fill")
            return
        }
        dispach(AddingData(obj))

        refName.current.value = ""
        refProfile.current.value = ""
        refID.current.value = ""
    }

    return (
        <Form style={style}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Id</Form.Label>
                <Form.Control ref={refID} type="number" placeholder="ID" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control ref={refName} type="text" placeholder="Name" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Profile</Form.Label>
                <Form.Control ref={refProfile} type="text" placeholder="Profile" />
            </Form.Group>

            <Button variant="primary" type="button" onClick={AddObject}>
                Submit
            </Button>
        </Form>
    );
}

export default FormSt;
