import { useEffect, useState } from "react"
import FormSec from "./FormSec"
import "./Movie.css"
import MovieCard from "./MovieCard"
import NodataFound from "./NoDataFound"
const movieData = {
    movies: [
        { id: 1, movieName: "A" },
        { id: 2, movieName: "B" },
        { id: 3, movieName: "C" },
        { id: 4, movieName: "D" },
        { id: 5, movieName: "E" }
    ],
    selectedMovie: []
}

function MainAap() {
    const [state, setState] = useState(movieData)

    const cloneState = { ...state }
    useEffect(() => {
        cloneState.selectedMovie = state.movies
        setState(cloneState)
    }, [])

    const handller = (val) => {
        console.log(val);
        if (val) {
            const filterdMovie = state.movies.filter((item) => {
                return item.movieName === val
            })
            cloneState.selectedMovie = filterdMovie
            setState(cloneState)
        }else{
            console.log("fff");
            setState(cloneState)
        }
        
    }
    const showSelected = () => {
        console.log(state.selectedMovie);
    }
    return <div className="mainMovie ">

        <FormSec handller={handller} />
        <div className="cards">
            {state.selectedMovie.length ? state.selectedMovie.map((item) => {
                return <MovieCard key={item.id} data={item} />
            }) : <NodataFound />}
        </div>
        <button type="button" disabled={state.selectedMovie.length ? false : true} onClick={showSelected} >Selected Movie</button>
    </div>
}
export default MainAap
