import { useRef } from "react"
import { Button, Form, InputGroup } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux";
import { incrementCount, decrementCount, updateGetObj } from "./slices/incrimentAndDecrement";

function ShowRedux() {
    const hrefInput = useRef()
    const hrefInput1 = useRef()
    const store = useSelector((state) => {
        return state
    })
    const dispach = useDispatch()

    const getValueINC = () => {
        const typeChenge = +hrefInput1.current.value
        dispach(incrementCount(typeChenge))
    }
    const getValueDIC = () => {
        const typeChenge = +hrefInput.current.value
        dispach(decrementCount(typeChenge))
    }

    const getObject = () => {
        dispach(updateGetObj({ inc: +hrefInput1.current.value, dec: +hrefInput.current.value }))
        console.log("see whats is in MainCom ");
        console.log(store.getObj);
    }


    return <>
        <div style={{ margin: "auto", width: 600 + "px" }}>
            <h1 style={{ marginTop: 20 + "px", marginBottom: 20 + "px" }}>Count : {store.count}</h1>
            <Form style={{ width: 500 + "px" }}>
                <InputGroup className="mb-3">
                    <Form.Control
                        placeholder="want to INC"
                        aria-label="Recipient's username"
                        aria-describedby="basic-addon2"
                        type="number"
                        ref={hrefInput1}


                    />
                    <Button variant="success" type="button" onClick={getValueINC} style={{ width: 140 + "px" }}>INC++</Button>
                </InputGroup>
                <InputGroup className="mb-3">
                    <Form.Control
                        placeholder="want to DEC"
                        aria-label="Recipient's username"
                        aria-describedby="basic-addon2"
                        type="number"
                        ref={hrefInput}
                    />
                    <Button variant="danger" onClick={getValueDIC} type="button" style={{ width: 140 + "px" }}>DIC--</Button>
                </InputGroup>
                <Button type="button" onClick={getObject}>Get Obj</Button>
            </Form>
        </div>
    </>
}
export default ShowRedux
