import { useState } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux"
import { postsThunk } from "./slicePosts/slicePosts";

function PostsThunk() {

    const styleCss = {
        width: "700px"
    }

    const btnCss = {
        width: "250px"
    }

    const localStore = useSelector((state) => {
        return state
    })
    const showUi = localStore.data.slice(0, 10)
    
    const dispacher = useDispatch()
    
    useState(() => {
        dispacher(postsThunk())
    }, [])

    return <div className="container">
        {localStore.status === "pending" && <h1>Loading....
            <div className="spinner-border" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        </h1>}

        {localStore.status === "fulfilled" && <Table striped bordered hover variant="">
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th style={styleCss}>Body</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {showUi.map((item) => {
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.title}</td>
                        <td style={styleCss}>{item.body}</td>
                        <td style={btnCss}>
                            <button type="button" className="btn btn-danger">Remove</button>
                            <button type="button" className="btn btn-warning mx-3">Edit</button>
                            <button type="button" className="btn btn-success">View</button>
                        </td>
                    </tr>
                })}
            </tbody>
        </Table>}


    </div>
}
export default PostsThunk
