import { useSelector } from "react-redux";
import FormInput from "./components/FormInput";
import TableCompo from "./components/TablularCompo";
import { useDispatch } from "react-redux";
import { useRef, useState } from "react";
import { addData, editData } from "./slices/sliceSection";

function CombineShow() {
    const localData = useSelector((state) => {
        return state
    })

    const refName = useRef()
    const refProfile = useRef()
    const refNumber = useRef()

    const clearInput = ""

    const dispach = useDispatch()

    const AddObject = () => {
        const obj = { name: refName.current.value, profile: refProfile.current.value, number: refNumber.current.value }
        if (!obj.name || !obj.profile || !obj.number) {
            alert("All input are not fill")
            return
        }
        dispach(addData(obj))

        refName.current.value = clearInput
        refProfile.current.value = clearInput
        refNumber.current.value = clearInput
    }

    const [Index, setIndex] = useState(null)
    const clone = [...localData.tabaleData]
    
    const editData = (item) => {
        refName.current.value = item.name
        refProfile.current.value = item.profile
        refNumber.current.value = item.number

        const index = clone.findIndex((obj) => {
            return obj.number === item.number
        })
        setIndex(index)
    }
    const Ubdated = () => {
        const obj = { name: refName.current.value, profile: refProfile.current.value, number: refNumber.current.value }
        if (!obj.name || !obj.profile || !obj.number) {
            alert("All input are not fill")
            return
        }
        const cloneNew = [...localData.tabaleData]
        cloneNew.splice(Index, 1, obj)
        // dispach(editData(clone))
        // refName.current.value = clearInput
        // refProfile.current.value = clearInput
        // refNumber.current.value = clearInput
    }
    return <div >
        <FormInput localData={localData} AddObject={AddObject} refs={{ refName, refProfile, refNumber }} Ubdated={Ubdated} />
        <TableCompo localData={localData} editData={editData} />
    </div>
}
export default CombineShow
