import { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"
import { useFetchContactsQuery } from "../../storeRTK/store"

function FormList() {
    const { data } = useFetchContactsQuery()
    const [state, setState] = useState({})
    const params = useParams()

    const onchenge = (e) => {
        const clone = {...state}
        clone[e.target.name] = e.target.value
        setState(clone)
    }
    useEffect(() => {
        if (data) {
            const findItem = data.find((item) => {
                return item.id === +params.id
            })
            setState(findItem)
        } else {
            return
        }
    }, [params])
    return <form className="w-50 p-3">
        <div className="mb-3">
            <label htmlFor="ID" className="form-label">ID</label>
            <input type="number" disabled name='id' className="form-control" placeholder="You can neither create nor update ID" id="exampleInputEmail1" aria-describedby="emailHelp" />
        </div>
        <div className="mb-3">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" name='name' value={state.name} className="form-control" id="exampleInputPassword1" />
        </div>
        <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="text" name='email' value={state.email} className="form-control" id="exampleInputPassword1" />
        </div>
        <div className="mb-3">
            <label htmlFor="addres" className="form-label">Addres</label>
            <input type="text" name='addres' value={state.addres} className="form-control" id="exampleInputPassword1" />
        </div>

        <Link to={'/lists'}><button type="button" >Submit</button></Link>
    </form>
}
export default FormList
