import { createContext, useReducer } from 'react'
import './All.css'
import Count from './Compo/Count'
import Value from './Compo/Value'

export const DataProvider = createContext()

const data = {
    count: 0,
    value: 0
}
const rootReducer = (state, action) => {

    // const ActionValue = (action, pos) => {
    //     if (action === 'INC') {
    //       state[pos]++
    //       return state
    //     }
    //     else if (action === 'DIC') {
    //         console.log(state);
    //     }
    // }

    switch (action.type) {
        case 'count':
            const newData = {...state}
            if (action.action === 'INC') {
                newData.count = newData.count + 1
            }
            return newData

        case 'value':
            // ActionValue(action.action, 'value')
            return state
    }
    return state
}

function MainReducer() {
    const [state, dispatch] = useReducer(rootReducer, data)
    return (
        <div className="div-box">
            <DataProvider.Provider value={{ state, dispatch }}>
                <Count />
                <Value />
            </DataProvider.Provider>

        </div>
    )
}
export default MainReducer
