import axios from "axios";
import { useEffect, useState } from "react";
import ButtonPage from "./ButtonPage"
import InputSec from "./InputSec"
import MainNews from "./MainNews"

let count = 0
const url = 'https://hn.algolia.com/api/v1/search?query='
function HackersNews() {
    const [News, setNews] = useState(null)
    const [loader, setloader] = useState(false)
    const getValue = async (event) => {
        setloader(true)
        const res = await axios.get(`${url}${event.target.value}&page=0`)
        setNews(res.data);
        setloader(false)
    }
    const ShowUi = async () => {
        const response = await axios.get(`${url}&page=0`)
        setNews(response.data)
    }
    useEffect(() => {
        ShowUi()
    }, [])
    
    const NextPage =async()=>{
        setloader(true)
        if (count ===49) {
            return
        }
        count = count + 1
        const response = await axios.get(`${url}&page=${count}`)
        setNews(response.data)
        setloader(false)
    }
    const PrevPage =async()=>{
        setloader(true)
        if (count === 0) {
            return
        }
        count = count - 1
        const response = await axios.get(`${url}&page=${count}`)
        setNews(response.data)
        setloader(false)
    }
    return (
        <div style={{ width: '1300px', margin: 'auto' }}>
            <InputSec getValue={getValue} />
            <ButtonPage NextPage={NextPage} PrevPage={PrevPage} count={count}/>
            <MainNews data={News} loader={loader} />
        </div>
    )
}
export default HackersNews
