import { useContext, useEffect, useState } from "react"
import { useParams } from "react-router-dom";
import { ProductDataProvider } from "../../Pages/productpage/ProductPage"
import './ProductComp.css'

function RightProduct() {
    const context = useContext(ProductDataProvider)
    const paramas = useParams();

    const { all, productDetail, productList } = context.state
    
    useEffect(() => {
        context.dispatch({ type: paramas.type })
    }, [paramas.type])

    // const [searchData, setSearchData] = useState(filterdProduct)
    // const [val, setVal] = useState()

    // const searchHandler = (e) => {
    //     const value = e.target.value
    //     const searcheditems = filterdProduct.filter((item) => {
    //         const pname = item.productName
    //         const data = pname.includes(value)
    //         return data
    //     })
    //     console.log('searcheditems', searcheditems);
    //     setSearchData(searcheditems)

    // }
    // console.log('searchData', searchData);
    return (
        <div>
            hello:
            <h1 className="heading">{context.state.title} Product</h1>
            <div className="input-group input-group-lg">
                <span className="input-group-text" id="inputGroup-sizing-lg">Search</span>
            </div>
            <hr />
            <div className="rigthComp">
                {productDetail.map((item) => {
                    return <div key={item.id} className="card" >
                        <img src={item.url} />
                        <div className="card-body">
                            <h5 className="card-title">Price $: {item.price}</h5>
                            <h5 className="card-title">Name : {item.productName}</h5>
                            <hr />
                            <p className="card-text">Discription : {item.discription}</p>
                            <span>Made in : {item.madeIn}</span>

                        </div>
                    </div>
                })}
            </div>
        </div>
    )
}
export default RightProduct
