import { useContext, useState } from "react"
import { useParams } from "react-router-dom";
import { ProductDataProvider } from "../../Pages/productpage/ProductPage"
import './ProductComp.css'

function RightProduct() {
    const context = useContext(ProductDataProvider)
    const params = useParams()
    const manupulateData = context.productDetail.filter((item) => {
        if (params.type === 'all') {
            return context.productDetail
        } else {
            return params.type === item.type
        }
    })
    const searchHandle = (e) => {
        const value = e.target.value
