import React, { useContext, useState } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { ProductDataProvider } from '../../Pages/productpage/ProductPage';
import './ProductComp.css'

function ModelProduct() {
    const context = useContext(ProductDataProvider)
    console.log(context);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [formVal, setFormVal] = useState({
        name: '',
        fullName: '',
        age: '',
        skills: [],
        gender: ''
    })


    const changeHandler = (e) => {
        const updatedForm = { ...formVal }
        updatedForm[e.target.name] = e.target.value;
        setFormVal(updatedForm)
    }
    const changeSkillsHandler = (str) => {
        const formD = { ...formVal }
        formD.skills.push(str)
        setFormVal(formD)
    }
    const submitForm = (e) => {
        e.preventDefault()
        console.log(formVal)
    }

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Launch demo modal
            </Button>

            <Modal className='modelForm' show={show} onHide={handleClose}>
                <Form>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Enter Name" name="name" value={formVal.name} onChange={changeHandler} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Full Name</Form.Label>
                        <Form.Control type="text" placeholder="Enter Full Name" name="fullName" value={formVal.fullName} onChange={changeHandler} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Age</Form.Label>
                        <Form.Control type="number" placeholder="Enter Age" name="age" value={formVal.age} onChange={changeHandler} />
                    </Form.Group>
                   
                    <Button variant="primary" type="button" onClick={(e) => submitForm(e)}>
                        Submit
                    </Button>
                </Form>
            </Modal>
        </>
    );
}
export default ModelProduct
