import { createSlice } from "@reduxjs/toolkit";

export const sliceProduction = createSlice({
    name: "printArry",
    initialState: {
        array: [
            { id: 1, name: "A" },
            { id: 2, name: "B" },
            { id: 3, name: "C" },
            { id: 4, name: "D" },
        ],
       
    },
    reducers: {
        setNewArr: (state) => {
            console.log(state.array);
        }
    }
})
export const {setNewArr} = sliceProduction.actions
