import { useContext } from "react"
import { Accordion } from "react-bootstrap";
import { AcordianProvider } from "../MainAcordian";
import { TiArrowRight } from "react-icons/ti";

function LeftAcordian(props) {
    // console.log(props);
    const context = useContext(AcordianProvider)

    const finding = context.state.states.filter((item) => {
        return item.groupID === props.value.groupID;
    });

    const DataAlert = (val) => {
        context.dispatch({ val})
    }

    return (
        <div>
            <Accordion defaultActiveKey={props.id}>
                <Accordion.Item eventKey={props.id}>
                    <Accordion.Header>{props.value.cName}</Accordion.Header>
                    <Accordion.Body>
                        <ul>
                            {finding.map((item, i) => {
                                return <li key={i} className="btn btn-danger" onClick={() => DataAlert(item.sName)}>
                                    <button>{item.sName}</button>
                                    {<TiArrowRight />}
                                </li>
                            })}
                        </ul>
                    </Accordion.Body>
                </Accordion.Item>
            </Accordion>

        </div>
    )
}
export default LeftAcordian
