import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const postsThunk = createAsyncThunk("allPosts", async (payload, { rejectWithValue }) => {
    
    if (payload.type === "remove") {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${payload.id}`)
        const data = response.data
        return data
    }

    // if (payload.type === "view") {
    //     console.log("find payy", payload);
    //     const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${payload}`)
    //     const data = response.data
    //     return data
    // } 

    else {
        const response = await axios.get('https://jsonplaceholder.typicode.com/posts')
        const data = response.data
        return data
    }
})

export const postSlice = createSlice({
    name: "userThunk",
    initialState: {
        data: [],
        selectedItem: {},
        status: ""
    },
    reducers: {
        removePost: (state, action) => {
            state.data = action.payload
        },
        showView: (state, action) => {
            state.selectedItem = action.payload
        }

    },
    extraReducers: (builder) => {

        builder.addCase(postsThunk.pending, (state, action) => {
            state.status = "pending"
        })

        builder.addCase(postsThunk.fulfilled, (state, action) => {
            state.data = action.payload.slice(0, 10)
            state.status = "fulfilled"
        })

        builder.addCase(postsThunk.rejected, (state, action) => {
            state.status = "rejected"
            state.data = action.error
        })
    }
})

export const { removePost, showView } = postSlice.actions
