import { useState } from 'react';
import './carts.css';
import Cartitem from './cartitem/Cartitem';
const data = [
    {heading:"Samsung Galaxy",price:'$399.99',button:'Remove'},
    {heading:"Google Pixel",price:'$499.99',button:'Remove'},
    {heading:"Xiaomi Redmi Note 2",price:'$699.99',button:'Remove'}
]


function Carts(props) {
    const [count, setCount] = useState(1);

    const incCount = ()=> {
        // console.log(count)
        setCount(count + 1)
    }
    console.log('cards render--')
    return (
        <div>
            <div className='main' >
                <div className='container'>
                    <div>UseReducer</div>
                    <div>c</div>    
                </div>
            </div>
            <div className='cart'>
                <div>
                    <h3>Count: {count}</h3>
                    <button type='button' onClick={incCount}>Inc Count</button>
                </div>
                <h2>YOUR BAG </h2>

                {data.map((item, index)=> {
                    return <Cartitem data={item} key={index}/>
                })}
                
                
                <footer>
                    <button className='btn'>CLEAR CART</button>
                </footer>

            </div>

        </div>
    )
}

export default Carts;
