import { createSlice } from "@reduxjs/toolkit";

export const storeSlice = createSlice({
    name: "rtk-query",
    initialState: {
        data: [],
        flag:true
    },
    reducers: {
        fillData: (state, action) => {
            state.data = action.payload
        }
    }
})
export const { fillData } = storeSlice.actions
