import { createSlice } from "@reduxjs/toolkit";

export const counterSlice = createSlice({
    name: "counter-slice",
    initialState: {
        count: 0
    },

    reducers: {

        incrementCount: (val, val2) => {
            

            cloneCount.count = cloneCount.count + val2.payload 
            val.count = cloneCount.count

        },
        decrementCount: (val, val2) => {
            val.count = val.count - val2.payload 
        }
    }

})

export const { incrementCount, decrementCount } = counterSlice.actions
