import { useRef, useState } from "react"
import { Button, Form, InputGroup } from "react-bootstrap"
import { useDispatch } from "react-redux";
import { incrementCount ,decrementCount} from "../../ReduxPrac/counterSlice/CounterSlice";

function ShowRedux() {
    const hrefInput = useRef(null)
    // console.log(hrefInput);
   const dispach = useDispatch()
   console.log(hrefInput.current.value);
    // const onChegeHandle = (e)=>{
        

    // }
    return <>
        <h1 style={{marginTop:20 + "px",marginBottom:20 + "px"}}>Count : </h1>
        <Form style={{ width: 500 + "px" }}>
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="want to INC"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    type="number"
                    // ref={hrefInput}
                   

                />
                <Button variant="success" onClick={()=>{dispach(incrementCount(hrefInput.current.value))}} style={{ width: 140 + "px" }}>INC</Button>
            </InputGroup>
            <InputGroup className="mb-3">
                <Form.Control
                    placeholder="want to DEC"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    type="number"
                />
                <Button variant="danger" style={{ width: 140 + "px" }}>DIC</Button>
            </InputGroup>
        </Form>
    </>
}
export default ShowRedux
