import { useState } from "react"
import { Button, Form } from "react-bootstrap"

function ProductInfo() {
    const [data, SetData] = useState({ name: '', fullName: '', age: '', gender: '', skill: [] })

    const onchangeHandler = (e) => {
        console.log(e.target.label);
        if (e === '') {
            
        }
        if (typeof e === 'string') {
            const obj = { ...data }
            const value = e
            obj['gender'] = value
            SetData(obj)
            
        } else {
            const obj = { ...data }
            const value = e.target.value
            obj[e.target.name] = value
            SetData(obj)
        }
       
    }

    const details = () => {
        console.log(data);
    }


    return (
        <div>
            <h1>Student Form</h1>
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Name :</Form.Label>
                    <Form.Control name="name" value={data.name} onChange={(e) => { onchangeHandler(e) }} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasic">
                    <Form.Label>Full Name :</Form.Label>
                    <Form.Control name="fullName" value={data.fullName} onChange={(e) => { onchangeHandler(e) }} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasic">
                    <Form.Label>Age :</Form.Label>
                    <Form.Control type="number" name="age" value={data.age} onChange={(e) => { onchangeHandler(e) }} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasic">
                    <Form.Label>Gender :</Form.Label>
                    <Form.Check type="radio" label="Male" name="gender" onChange={() => { onchangeHandler('Male') }} />
                    <Form.Check type="radio" label="Female" name="gender" onChange={() => { onchangeHandler('Female') }} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                    <Form.Label>Skills :</Form.Label>
                    <div className="check">
                        <Form.Check type="checkbox" label="JavaScript"  onChange={(label) => { onchangeHandler(label) }} />
                        <Form.Check type="checkbox" label="TypeScript"  onChange={(label) => { onchangeHandler(label) }} />
                        <Form.Check type="checkbox" label="Html"  onChange={(label) => { onchangeHandler(label) }} />
                    </div>
                </Form.Group>
                <Button onClick={details} >
                    Submit
                </Button>
            </Form>
        </div>
    )
}
export default ProductInfo
