import axios from "axios";
import { useEffect, useState } from "react";
import { Routes, Route } from "react-router-dom";
import Details from "./Details"
import Posts from "./Posts"

function Main() {
    const [data, setData] = useState(null)
    const [detail, setDetail] = useState(null)

    const getPosts = async () => {
        const response = await axios.get('https://jsonplaceholder.typicode.com/posts')
        const currentval = response.data.slice(0, 8)
        setData(currentval)
    }
    useEffect(() => {
        getPosts()
    }, [])

    const showData = async (val) => {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${val}`)
        setDetail(response.data)

    }
    return (
       
         <Routes>
         <Route path="/" element={<Posts data={data} showData={showData} />} />
         <Route path="detail" element={<Details data={detail} />} />
     </Routes>
    )
}
export default Main
