import { createSlice } from "@reduxjs/toolkit";

export const counterSlice = createSlice({
    name: "counter-slice",
    initialState: {
        count: 0
    },
    reducers: {
        incrementCount: (val) => {
            val.count += 1
        },
        decrementCount: (val) => {
            val.count -= 1
        }
    }

})

export const { incrementCount, decrementCount } = counterSlice.actions
