        obj[e.target.name] = val
        setInputval(obj)
    }
    const submitForm = (event) => {
        event.preventDefault()

    }
    return (
        <>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>age</th>
                    </tr>
                </thead>
                <tbody>
                    {data.arr.map((item, i) => {
                        return <tr key={i}>
                            <td>{item.name}</td>
                            <td>{item.age}</td>
                        </tr>
                    })}
                </tbody>
            </table>


            <form onSubmit={submitForm}>
                <input name='name' placeholder='name' onChange={onChangeHandler}/>
                <input name='age' placeholder='age' onChange={onChangeHandler} />
                <button>Add</button>
            </form>

        </>
    )
}
export default InChild
