import { useEffect, useRef, useState } from 'react'
import { firstName } from './Parent'
function InChild({ data ,addValue}) {

    const refName = useRef(null)
    const refAge = useRef(null)
    const submitForm = (event) => {
        event.preventDefault()
        const obj = { name: refName.current.value, age: refAge.current.value }
        addValue(obj)
        
    }
    useEffect(()=> {
        // refName.current.style.backgroundColor = 'red'
        // refName.current.style.marginLeft = '20px'
    }, [])
    return (
        <>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>age</th>
                    </tr>
                </thead>
                <tbody>
                    {data.arr.map((item, i) => {
                        return <tr key={i}>
                            <td>{item.name}</td>
                            <td>{item.age}</td>
                        </tr>
                    })}
                </tbody>
            </table>


            <form onSubmit={submitForm}>
                <input ref={refName} name='name' placeholder='name' />
                <input ref={refAge} name='age' placeholder='age'  />
                <button>Add</button>
            </form>

           

        </>
    )
}
export default InChild
