import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import { RemoveData } from "../centerSlcie/newSlice";

function Students() {
    const localStore = useSelector((state) => {
        return state
    })

    const dispach = useDispatch()
    const removeItem = (item) => {
        const newClone = [...localStore.data]
        const filterdData = newClone.filter((data) => {
            return data.name !== item.name
        })
        dispach(RemoveData(filterdData))

    }

    const EditData = (id)=>{
        Navigate(`/form/id:${id}`)
    }
    return <>
        <h1 style={{ margin: "30px 0" }}>{localStore.heading}</h1>
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Profile</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {localStore.data && localStore.data.map((item) => {
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.profile}</td>
                        <td>
                            <button onClick={() => { removeItem(item) }}>Del</button>
                            <button onClick={()=>{EditData(item.id)}}>Edit</button>
                        </td>
                    </tr>
                })}


            </tbody>
        </Table>
    </>
}
export default Students
