import { useEffect, useState } from "react"
import FormSec from "./FormSec"
import "./Movie.css"
import MovieCard from "./MovieCard"
const movieData = {
    movies: [
        { id: 1, movieName: "A" },
        { id: 2, movieName: "B" },
        { id: 3, movieName: "C" },
        { id: 4, movieName: "D" },
        { id: 5, movieName: "E" }
    ],
    selectedMovie: []
}

function MainAap() {
    const [state, setState] = useState(movieData)

    useEffect(()=>{
        state.selectedMovie = state.movies
    },[])

    const handller = (val) => {
        const cloneState = {...state}
        const filterdMovie = cloneState.movies.filter((item)=>{
            // console.log(item);
            return item.movieName === val
        })
        cloneState.selectedMovie = filterdMovie
        setState(cloneState)
    }
    return <div className="mainMovie ">

        <FormSec handller={handller} />
        <div className="cards">
            {state.selectedMovie.length ?<h2>No Deta Found......</h2>  : state.movies.map((item) => {
                return <MovieCard key={item.id} data={item}/>
            })}
        </div>
        <button>Selected Movie</button>
    </div>
}
export default MainAap
