import axios from 'axios';
import { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';

const url = 'http://localhost:5000/contact'
function AllList() {

    const [data, setData] = useState(null)

    const getContactList = async () => {
        const response = await axios.get(url)
        setData(response.data)
    }

    // const handleDelete = async (id) => {
    //     try {
    //         const res = await axios.delete(`${url}/${id}`)
    //         getContactList()
    //     } catch (error) {

    //     }
    // }

    useEffect(() => {
        getContactList()
    }, [])
    return (<>
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>#</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Username</th>
                </tr>
            </thead>
            <tbody>
                {data && data.map((item) => {
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.email}</td>
                        <td>{item.addres}</td>
                        <td>
                            <Link to='/list/edit/2'><Button variant="warning">Update</Button></Link>
                            <Link to='/list/view/2'><Button variant="info"  className='mx-3'>View</Button></Link>
                            <Button variant="danger" onClick={()=>alert("delete")}>Delete</Button>
                        </td>
                    </tr>
                })}


            </tbody>
        </Table>
        <Link to={'/list/add'}><Button>Add</Button></Link></>
    );
}

export default AllList;
