import { useState } from "react"

function FormSection() {
    const obj = {
        name: 'john',
        age: "50"
    }
    const [state, setState] = useState(obj)
    const [show, setshow] = useState(false)

    const onChange = (e) => {
        const newobj = { ...state }
        newobj[e.target.name] = e.target.value
        if (newobj.name === "" || newobj.age === "") {
            setshow(true)
        } else {
            setshow(false)
        }
        setState(newobj)

    }
    const dataSet = () => {
        console.log(state);
    }


    return (
        <form  >
            <input value={state.name} name='name' className="d-block mb-3 mt-3" onChange={onChange} />
            <input value={state.age} name='age' className="d-block mb-3" onChange={onChange} />
            <button onClick={dataSet} type='button' disabled={show} >Submit</button>
        </form>
    )
}
export default FormSection
