import { createSlice } from "@reduxjs/toolkit";

export const comboSlice = createSlice({
    name: "combo Slice",
    initialState: {
        tabaleData: []
    },
    reducers: {
        addData: (state, val) => {
            const clone = [...state.tabaleData, val.payload]
            state.tabaleData = clone
        },
        removedData: (state, val) => {
            state.tabaleData = val.payload
        },
        editData: (state) => {
            console.log("hello edit");
        }
    }
})
export const { addData, removedData } = comboSlice.actions
