                </div>
            </div>
            <div className='cart'>
                <div>
                    <h3>Count: {count}</h3>
                    <button>Inc Count</button>
                </div>
                <h2>YOUR BAG </h2>

                {data.map((item, index)=> {
                    return <Cartitem data={item} key={index}/>
