import { useState } from "react";
import { InputGroup } from "react-bootstrap"
import { useParams } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function InputForm({ dispatch }) {
    const [val, setVal] = useState('')
    const parmas = useParams()

    const onhandler = (e) => {
        setVal(e.target.value)
    }

    const onSearch = (e) => {
        e.preventDefault()
        dispatch({ type: parmas.type, input: val })
        setVal('')
    }
     
    return (
        <div>
            <Form name="searchForm" onSubmit={onSearch}>
                <InputGroup size="lg">
                    <Form.Control
                        value={val}
                        onChange={onhandler}
                        aria-label="Large"
                        aria-describedby="inputGroup-sizing-sm"
                    />
                    <Button type="submit" variant="success" disabled={val ? false : true}>Search Item</Button>
                    <Button variant="info">Reset</Button>
                </InputGroup>
            </Form>
        </div>
    )
}
export default InputForm
