import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const postsThunk = createAsyncThunk("allPosts", async (payload, { rejectWithValue }) => {
    if (payload) {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/${payload}`)
        const data = response.data
        return data
    } else {
        const response = await axios.get('https://jsonplaceholder.typicode.com/posts')
        const data = response.data
        return data
    }
})

export const postSlice = createSlice({
    name: "userThunk",
    initialState: {
        data: [],
        status: ""
    },
    reducers: {
        removePost: () => {
            console.log("call");
        }
    },
    extraReducers: (builder) => {

        builder.addCase(postsThunk.pending, (state, action) => {
            state.status = "pending"
        })

        builder.addCase(postsThunk.fulfilled, (state, action) => {
            action.payload.slice(0, 10)
            state.data = action.payload
            state.status = "fulfilled"
        })

        builder.addCase(postsThunk.rejected, (state, action) => {
            state.status = "rejected"
            state.data = action.error
        })
    }
})

export const { removePost } = postSlice.actions
