import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function FormPage(params) {
    const [obj, SetObj] = useState(
        {
            id: '',
            imgUrl: '',
            details: '',
        }
    )
const handleChange = (e) => {
    const updatedForm = { ...obj }
    updatedForm[e.target.name] = e.target.value;
    SetObj(updatedForm)
}
console.log(obj);
return <div>
    <Form>
        <Form.Group className="mb-3" >
            <Form.Label>ID :</Form.Label>
            <Form.Control type='number' name='id' value={obj.id} onChange={handleChange} />

        </Form.Group>
        <Form.Group className="mb-3" >
            <Form.Label>IMG :</Form.Label>
            <Form.Control type='text' name='imgUrl' value={obj.imgUrl} onChange={handleChange} />

        </Form.Group>

        <Form.Group className="mb-3" >
            <Form.Label>details :</Form.Label>
            <Form.Control type='text' name='details' value={obj.details} onChange={handleChange} />
        </Form.Group>

        <Button onClick={()=>{addData(obj)}}>Submit</Button>
    </Form>

</div>
}
export default FormPage
