import { useDispatch, useSelector } from "react-redux"
import Card from "./components/Card"
import FormSection from "./components/FormSection"
import { setNewArr } from "./Slices/sliceProduction"

function UserInterface() {
    const boxCss = {
        border: "1px solid black",
        width: "400px",
        padding: "20px"
    }
    const localStore = useSelector((state) => {
        return state
    })
    const dispatch = useDispatch()
    const deleteObject = (str) => {
        const filterdData = localStore.array.filter((item) => {
            return item.name !== str
        })
        dispatch(setNewArr(filterdData))
    }

    return <div style={boxCss}>
        {localStore.array.map((item) => {
            return <Card key={item.id} data={item} deleteObject={deleteObject} />
        })}
        <FormSection/>
    </div>
}
export default UserInterface
