function Parent() {
    const [val, setVal] = useState(arr)
    const addValue = (item) => {
        const newData = [...val]
        newData.push(item)
        setVal(newData)
    }
    // const refUse = useRef()
    // const getData =()=>{
    //     console.log(refUse.current);
    //     console.log(refUse.current.name);
    //     console.log(refUse.current.value);
    // } 

    return (
        <>
            {/* <input  ref={refUse}  name='name' />
            <button onClick={getData}>click</button> */}
            
            <Child arr={val} addValue={addValue} />
            <input  type={'text'} />

        </>
    )
}
export default Parent
