import { useState } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux"
import { postsThunk, removePost } from "./slicePosts/slicePosts";

function PostsThunk() {

    const styleCss = {
        width: "700px"
    }

    const btnCss = {
        width: "250px"
    }

    const localStore = useSelector((state) => {
        return state
    })

    const dispacher = useDispatch()
    useState(() => {
        dispacher(postsThunk("first"))
    }, [])

    const removeItem = (item) => {
        const filterdArr = localStore.data.filter((value) => {
            return item.id !== value.id
        })
        dispacher(removePost(filterdArr))
    }

    const viewItem = () => {

    }

    return <div className="container">
        {localStore.status === "pending" && <h1>Loading....
            <div className="spinner-border" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        </h1>}

        {localStore.status === "fulfilled" && <Table striped bordered hover >
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th style={styleCss}>Body</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {localStore.data.map((item) => {
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.title}</td>
                        <td style={styleCss}>{item.body}</td>
                        <td style={btnCss}>
                            <button type="button" className="btn btn-danger" onClick={() => removeItem(item)}>Remove</button>
                            <button type="button" className="btn btn-warning mx-3">Edit</button>
                            <button type="button" className="btn btn-success" onClick={() => viewItem(item)}>View</button>
                        </td>
                    </tr>
                })}
            </tbody>
        </Table>}

        {localStore.status === "rejected" && <h1>{localStore.data.message}</h1>}


    </div>
}
export default PostsThunk
