import { Button } from 'react-bootstrap';
import Table from 'react-bootstrap/Table';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { useDeleteListMutation, useFetchContactsQuery } from '../../storeRTK/store';

function AllList() {
    const { data } = useFetchContactsQuery()
    const item = useDeleteListMutation()
    const functionDelete = item[0]
    const dispach = useDispatch()
    const deleteList = async (id) => {
        functionDelete(id)
        dispach(useFetchContactsQuery())
    }

    return (<>
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>#</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Username</th>
                </tr>
            </thead>
            <tbody>
                {data && data.map((item) => {
                    return <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.email}</td>
                        <td>{item.addres}</td>
                        <td>
                            <Link to='/list/edit/2'><Button variant="warning">Update</Button></Link>
                            <Link to='/list/view/2'><Button variant="info" className='mx-3'>View</Button></Link>
                            <Button variant="danger" onClick={() => deleteList(item.id)}>Delete</Button>
                        </td>
                    </tr>
                })}


            </tbody>
        </Table>
        <Link to={'/list/add'}><Button size="lg mx-4">AddList</Button></Link>
    </>
    );
}

export default AllList;
