import { createContext, useReducer } from "react"
import { Link, Route, Routes } from "react-router-dom"
import Detail from "./Detail"
import FormPage from "./galleryCom/FormPage"
import GalleryCom from "./galleryCom/GalleryCom"
import ProductCom from "./productCom/ProductCom"

const data = {
  gallery: {
    title: 'Gallery',
    galleryData: [
      { id: 1, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
      { id: 2, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
      { id: 3, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
      { id: 4, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
      { id: 5, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
      { id: 6, imgUrl: 'https://images.pexels.com/photos/2893685/pexels-photo-2893685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', details: 'hellow' },
    ]
  }
}
export const dataContext = createContext()

const reducer = (state, action) => {
  console.log(state);
  const newData = { ...data.gallery }
  const newGallery = [...newData.galleryData]
  newGallery.push(action)
  newData.galleryData = newGallery

//  state.gallery.galleryData.push(action)
}

function ReactProject(params) {
  const [val, dispatch] = useReducer(reducer, data)
  const addData = (obj) => {
    // console.log(obj);
    dispatch(obj)
  }
  return <div >
    <Link to={'/'}>gallary</Link>
    <Link to={'product'}>Product</Link>
    <Routes>
      <Route path="/" element={<dataContext.Provider value={data}><GalleryCom /></dataContext.Provider>} />
      <Route path="/product" element={<ProductCom />} />
      <Route path='detail/:id' element={<Detail data={data} />} />
      <Route path="form" element={<FormPage addData={addData} />} />
    </Routes>
  </div>
}
export default ReactProject
