import { useState } from "react";
import { Button, Form, InputGroup } from "react-bootstrap"

function InputForm() {
    const [val, setVal] = useState('')

    const onhandler = (e) => {
        setVal(e.target.value)
        // console.log(e.target.value);
    }

    const onSearch = (e) => {
        e.preventDefault()
        setVal('')
        // console.log(val);
    }
    return (
        <div>
            <InputGroup size="lg">
                <Form.Control
                    value={val}
                    onChange={onhandler}
                    aria-label="Large"
                    aria-describedby="inputGroup-sizing-sm"
                />
                <Button variant="success" onClick={onSearch}>Search Item</Button>
            </InputGroup>
        </div>
    )
}
export default InputForm
