import { Link, Route, Routes } from "react-router-dom"
import ComA from "./Pages/ComA"
import ComB from "./Pages/ComB"
import './MainContext.css'
import { createContext, useState } from "react"
const obj = {
    a: ['a', 'b', 'c'],
    b: ['d', 'e', 'f']
}
export const creatingContext = createContext(obj)
function MainContext() {
    const[val,setVal]=useState(obj)
    const creatingnewValue=(value)=>{
     let dd=  obj.a.push(value)
      setVal(dd)
    }
    return <div className="container">
        <div className="box">
            <Link to={'/'} className={'link'}>ABC</Link>
            <Link to={'comB'} className={'link'}>DEF</Link>
        </div>
        <creatingContext.Provider value={{val:val,creatingnewValue:creatingnewValue}}>
            <Routes>
                <Route path="/" element={<ComA />} />
                <Route path="comB" element={<ComB />} />
            </Routes>
        </creatingContext.Provider>

    </div>
}
export default MainContext
