import { useState } from "react";
import { useReducer } from "react";

const fnReducer = (state, action) => {
    const newObj = { ...state }
    if (action.target.name === "name") {
        newObj.name = action.target.value
    }
    else {
        newObj.age = action.target.value
    }
    return newObj
}


function FormSection() {
    const obj = {
        name: 'john',
        age: 50
    }
    const [data, dispatch] = useReducer(fnReducer, obj)



    const collectData = (e) => {
        e.preventdefault()
    }
    return (
        <form onSubmit={collectData}>
            <input value={data.name} name='name' placeholder="name" className="d-block mb-3 mt-3" onChange={dispatch} />
            <input value={data.age} name='age' placeholder="age" className="d-block mb-3" onChange={dispatch} />
            <button type="button">Submit</button>
        </form>
    )
}
export default FormSection
