import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { contactsApi, useFetchContactsQuery } from "../store/store";

const url = 'http://localhost:5000/contacts'
function Contacts() {
    const [data, setData] = useState(null)
    // console.log(data);
    // const [isLoader, setIsLoader] = useState(false)
    const { data: contactsData, error, isLoading } = useFetchContactsQuery();

    const getContactList = async () => {
        const response = await axios.get(url)
        
      console.log(response);
        setData(response.data)
    }
    const handleDelete = async (id) => {
        try {
            const res = await axios.delete(`${url}/${id}`)
            getContactList()
        } catch (error) {

        }
    }
    useEffect(() => {
        getContactList()
    }, [])
    console.log('contactsData----', contactsData, isLoading)
    return (
        <div className="App">

            <table border={'1'} className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data && data.map((item) => {
                        return <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.email}</td>
                            <td>
                                <Link to={`/contacts/edit/${item.id}`}>
                                    <button>Edit</button>
                                </Link>
                                <button onClick={() => handleDelete(item.id)}>Del</button>
                                <Link to={`/contacts/${item.id}`}>
                                    <button>View</button>
                                </Link>
                            </td>
                        </tr>
                    })}
                </tbody>
            </table>
        </div>


    );
}

export default Contacts;
