import LeftAcordian from "./Components/LeftAcordian"
import RightAcordian from "./Components/RightAcordian"
import './Acordian.css'
import { createContext, useReducer } from "react"

const data = {
    countries: [
        { id: 1, groupId: 1, cName: 'INDIA' },
        { id: 2, groupId: 2, cName: 'US' },
    ],
    states: [
        { id: 1, groupId: 1, sName: 'A' },
        { id: 2, groupId: 1, sName: 'B' },
        { id: 3, groupId: 2, sName: 'C' },
        { id: 4, groupId: 2, sName: 'D' },
    ],
    selectedData: []
}

export const AcordianProvider = createContext()

const rootReducer = (state, action) => {
    // console.log(action);
    if (action.set) {

        const newClone = { ...state, selectedData: [...state.selectedData] }

        const newStates = newClone.selectedData.filter((logs) => {
            return logs.sName === action.val
        })
        newClone.states = [...state.states, newStates[0]]

        const newSelectedData = newClone.selectedData.filter((item) => {
            return item.sName !== action.val
        })
        newClone.selectedData = newSelectedData

        return newClone
    }
    if (action) {
        const newData = { ...state, selectedData: [] }

        const filterd = state.states.filter((item) => {
            return item.sName === action.val
        })
        newData.selectedData = [...state.selectedData, filterd[0]]

        if (filterd.length) {
            const leftFIlterd = state.states.filter((item) => {
                return item.sName !== action.val
            })
            newData.states = leftFIlterd
        }
        return newData
    }

    return state
}

function MainAcordian() {
    const [state, dispatch] = useReducer(rootReducer, data)
    return (
        <div className="d-flex Acordian">
            <AcordianProvider.Provider value={{ state, dispatch }}>
                <div>
                    {state.countries.map((item) => {
                        return <LeftAcordian key={Math.random()} id={item.id} value={item} />
                    })}
                </div>
                <RightAcordian />
            </AcordianProvider.Provider>

        </div>
    )
}
export default MainAcordian
