import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const myCreateAsyncThunk = createAsyncThunk('user-async', async (paylode, { rejectWithValue }) => {
    const response = await axios.get('https://jsonplaceholder.typicode.com/users')
    const data = response.data
    if (response.status < 200 || response.status >= 300) {
        return rejectWithValue(data)
    }
    return data
})
const initialState = {
    data: [],
    status: ""
} 
export const userSlice = createSlice({
    name: "users",
    initialState,
    reducers: {
        getDate: (state) => {

        }
    },
    extraReducers: (builder) => {

        builder.addCase(myCreateAsyncThunk.pending, (state, action) => {
            state.status = 'pending'
        })

        builder.addCase(myCreateAsyncThunk.fulfilled, (state, action) => {
            state.status = 'fulfilled'
            state.data = action.payload
        })

        builder.addCase(myCreateAsyncThunk.rejected, (state, action) => {
            state.status = 'rejected'
        })
    }
})
export const { getDate } = userSlice.actions
